import { HeaderActionTypes } from './user.types';

const defaultState = {
  loggedIn: false,
  user: {},
};

const userReducer = (state = defaultState, action) => {
  switch (action.type) {
    case HeaderActionTypes.SET_USER:
      return {
        loggedIn: true,
        user: { ...action.payload },
      };
    case HeaderActionTypes.LOG_OUT:
      localStorage.clear();
      return {
        loggedIn: false,
        user: {},
      };
    default:
      return state;
  }
};

export default userReducer;
